package smos.exception;

import java.lang.Exception;

/**
  * This class is the default exception generated by the system.
  */
public class DefaultException extends Exception {
	
	private static final long serialVersionUID = -8985617134055655964L;

	/**
	 * Generate the exception without an associated error message.
	 */
	public DefaultException() {
		super();
	}
	
	/**
	  * Generate the exception with an associated error message.
	  *
	  * @param pMessage 	The error message to be associated
      *                     to the exception.
	  */
	public DefaultException(String pMessage) {
		super(pMessage);
	}
	
	
}